
public class IncrementDemo {

	public static void main(String[] args) {
		   int a=1;
		   
		    System.out.println(a++);
		    System.out.println(a++);
		    System.out.println(++a);
		 
		    System.out.println(a++);
		    System.out.println(a++);
		 
		    System.out.println(a--);
		    System.out.println(a--);
		 
		    System.out.println(--a);
		    System.out.println(--a);
		    System.out.println(a--);
		    boolean greaterThan= a>0;
		    System.out.println("The value of a is greater than 0:  "+greaterThan);
		// TODO Auto-generated method stub

	}

}
